mapper.select:
  	select 
  	
  	from activity_zones
  	where delete_at is null
  	
		and store_uuid = #{storeUuid,jdbcType=VARCHAR}
	
	and type = 'activity'
  	order by create_datetime desc LIMIT #{start},#{end}

select count(1) from activity_zones where delete_at is null
and store_uuid = #{storeUuid,jdbcType=VARCHAR}
and type = 'activity'

  	select 
  	
  	from activity_zones
  	where delete_at is null
  	
		and store_uuid = #{storeUuid,jdbcType=VARCHAR}
	
	and type = 'host'
  	order by create_datetime desc LIMIT #{start},#{end}

select count(1) from activity_zones where delete_at is null
and store_uuid = #{storeUuid,jdbcType=VARCHAR}
and type = 'host'

	select
	cast( a.create_datetime as char(32) ) AS create_datetime, 
  g.good_name,g.cover,g.good_code,g.good_status,a.title,a.is_stop,
  a.activity_uuid,a.currentIndex
	from activity_zones AS a INNER JOIN goods AS g ON a.good_uuid = g.good_uuid
	where  a.type = 'host'
	
		and g.good_name like CONCAT(CONCAT('%', #{goodname,jdbcType=VARCHAR}), '%')
	
	
		and g.good_code = #{goodcode,jdbcType=VARCHAR}
	
	
		and g.good_status = #{goodstatus,jdbcType=VARCHAR}
	
   
		and a.store_uuid = #{storeUuid,jdbcType=VARCHAR}
	
	order by a.currentIndex asc LIMIT #{start},#{end}

select count(1) from activity_zones AS a INNER JOIN goods AS g ON a.good_uuid = g.good_uuid
where a.type = 'host'

	and g.good_name like CONCAT(CONCAT('%', #{goodname,jdbcType=VARCHAR}), '%')


	and g.good_code = #{goodcode,jdbcType=VARCHAR}


	and g.good_status = #{goodstatus,jdbcType=VARCHAR}

 
	and a.store_uuid = #{storeUuid,jdbcType=VARCHAR}

