mapper.select:
  	select 
  	
  	from dailys
  	where  delete_at is null
	
		and type = #{type,jdbcType=VARCHAR}
	 
	 
  		and start_time >= #{startTime,jdbcType=VARCHAR}
  	
  	
  		and end_time <= #{endTime,jdbcType=VARCHAR}
  	
  	order by create_datetime desc LIMIT #{start},#{end}

  	select count(1)
  	from dailys
  	where delete_at is null
     
		and type = #{type,jdbcType=VARCHAR}
	 
	 
  		and start_time >= #{startTime,jdbcType=VARCHAR}
  	
  	
  		and end_time <= #{endTime,jdbcType=VARCHAR}
