		and unit_price >=#{minPrice,jdbcType=DECIMAL}
	
	
		and unit_price <=#{maxPrice,jdbcType=DECIMAL}
	
	
		and good_status =#{goodStatus,jdbcType=DECIMAL}


 	select  
 	
 	,
 	
 	from goods 
 	where delete_at is null
 	
 		and good_name like CONCAT(CONCAT('%', #{goodName,jdbcType=VARCHAR}), '%')
 	
 	
 		and firt_category_name= #{firtCategoryName,jdbcType=VARCHAR}
 	
 	
 		and good_status = #{goodStatus,jdbcType=VARCHAR}
 	
 	
 		and unit_price >=#{minPrice,jdbcType=DECIMAL}
 	
 	
