mapper.select:
select
from users u LEFT JOIN user_address ud on u.user_uuid = ud.user_uuid where u.delete_at is null
and u.name like CONCAT(CONCAT('%', #{name,jdbcType=VARCHAR}), '%')
and u.nickname like CONCAT(CONCAT('%', #{nickname,jdbcType=VARCHAR}), '%')
and u.phone = #{phone,jdbcType=VARCHAR}
order by u.create_datetime desc LIMIT #{start},#{end}

  	select 
  	
  	from users u  LEFT JOIN user_address ud on u.user_uuid = ud.user_uuid
  	where u.delete_at is null
  	and u.user_uuid not in(
		SELECT user_uuid from users 
			where member_card_number is null
	)
  	
  		and u.name like CONCAT(CONCAT('%', #{name,jdbcType=VARCHAR}), '%')
  	
  	
  		and u.nickname like CONCAT(CONCAT('%', #{nickname,jdbcType=VARCHAR}), '%')
  	
  	
  		and u.phone = #{phone,jdbcType=VARCHAR}
  	
  	
  		and u.member_card_number = #{memberCardNumber,jdbcType=VARCHAR}
  	
  	order by u.create_datetime desc LIMIT #{start},#{end}

select count(1) from users u LEFT JOIN user_address ud on u.user_uuid = ud.user_uuid where u.delete_at is null and u.user_uuid not in( SELECT user_uuid from users where member_card_number is null )
and u.name like CONCAT(CONCAT('%', #{name,jdbcType=VARCHAR}), '%')
and u.nickname like CONCAT(CONCAT('%', #{nickname,jdbcType=VARCHAR}), '%')
and u.phone = #{phone,jdbcType=VARCHAR}
and u.member_card_number = #{memberCardNumber,jdbcType=VARCHAR}

